    delBtn.addEventListener("click", () => deleteEntry(entry));

    actions.appendChild(editBtn);
    actions.appendChild(delBtn);

    item.appendChild(label);
    item.appendChild(actions);
    entriesEl.appendChild(item);
  });
}

